(function($)
{$.fn.badger=function(badge,type,unique,callback)
{var badgerExists=this.find('#Badger'+unique+'.'+type+'-outter').html();if(!badge)
{if(badgerExists)
{this.find('#Badger'+unique+'.'+type+'-outter').remove();}}
else
{var oldBadge=this.find('#Badge').text();if(badge.charAt(0)=='+')
{if(isNaN(badge.substr(1)))
{badge=oldBadge+badge.substr(1);}
else
{badge=Math.round(Number(oldBadge)+Number(badge.substr(1)));}}
else if(badge.charAt(0)=='-')
{if(isNaN(badge.substr(1)))
{badge=oldBadge-badge.substr(1);}
else
{badge=Math.round(Number(oldBadge)-Number(badge.substr(1)));}}
if(badgerExists)
{this.find('#Badge').html(badge);}
else
{this.append('<div class="'+type+'-outter" id="Badger'+unique+'"><div class="'+type+'-inner"><p class="'+type+'-badge" id="Badge">'+badge+'</p></div></div>');}
if(isNaN(badge))
{this.find('#Badge').removeClass(type+'-number').addClass(type+'-text');}
else
{this.find('#Badge').removeClass(type+'-text').addClass(type+'-number');}
if(callback){callback(badge);} return this.find('#Badger'+unique);}};})(jQuery);